me";s:14:"simone-elkeles";s:7:"to_ping";s:0:"";s:6:"pinged";s:0:"";s:13:"post_modified";s:19:"2012-11-13 15:37:57";s:17:"post_modified_gmt";s:19:"2012-11-13 15:37:57";s:21:"post_content_filtered";s:0:"";s:11:"post_parent";s:1:"0";s:4:"guid";s:62:"http://localhost/macmillian_site/?post_type=speaker&#038;p=224";s:10:"menu_order";s:1:"0";s:9:"post_type";s:7:"speaker";s:14:"post_mime_type";s:0:"";s:13:"comment_count";s:1:"0";}i:232;O:8:"stdClass":23:{s:2:"ID";s:3:"220";s:11:"post_author";s:1:"4";s:9:"post_date";s:19:"2012-11-13 15:30:18";s:13:"post_date_gmt";s:19:"2012-11-13 15:30:18";s:12:"post_content";s:689:"<div id="ctl00_cphMainContent_divSpeakerDesc">
